package src.leetcode_sept

fun findTheDifference(s: String, t: String): Char {
    var c = t[t.length - 1].toInt()
    for (i in 0 until t.length - 1) {
        c = c.xor(s[i].toInt())
        c = c.xor(t[i].toInt())
    }
    return c.toChar()
}

fun main() {
    println(findTheDifference("abcd", "abcde"))
    println(findTheDifference("a", "aa"))
    println(findTheDifference("ae", "aea"))
    println(findTheDifference(
            "",
            ""
    ))
}
